import React, { useState, useEffect } from 'react';
import CycloneDashboard from './components/CycloneDashboard';
import StormMap from './components/StormMap';
import StationMonitor from './components/StationMonitor';
import AlertCard from './components/AlertCard';
import { dataService } from './services/api';
import './App.css';

// Mock data for development - replace with actual API calls
const mockStormData = [
  {
    storm_id: 'AL012025',
    storm_name: 'FRANKLIN',
    category: 'Category 3',
    alert_level: 'danger',
    emoji: 'üö®',
    current_position: {
      lat: 25.5,
      lon: -75.0,
      timestamp: '2025-08-31T07:30:00Z'
    },
    wind_speed_knots: 105,
    wind_speed_mph: 121,
    pressure_mb: 960,
    movement: {
      speed_knots: 12,
      direction_cardinal: 'WNW'
    },
    nearest_station: {
      station_id: 'MIA',
      station_name: 'Miami',
      distance_km: 85.2
    },
    warnings: [
      {
        type: 'high_wind',
        message: 'Dangerous winds of 105 knots (121.0 mph)'
      },
      {
        type: 'proximity_warning',
        message: 'Storm is 85.2 km from Miami'
      }
    ]
  },
  {
    storm_id: 'AL022025',
    storm_name: 'GERT',
    category: 'Tropical Storm',
    alert_level: 'warning',
    emoji: '‚ö†Ô∏è',
    current_position: {
      lat: 28.2,
      lon: -82.5,
      timestamp: '2025-08-31T07:30:00Z'
    },
    wind_speed_knots: 45,
    wind_speed_mph: 52,
    pressure_mb: 995,
    movement: {
      speed_knots: 8,
      direction_cardinal: 'N'
    },
    nearest_station: {
      station_id: 'TPA',
      station_name: 'Tampa',
      distance_km: 25.8
    },
    warnings: [
      {
        type: 'proximity_warning',
        message: 'Storm is 25.8 km from Tampa'
      }
    ]
  }
];

const mockStations = {
  'MIA': { name: 'Miami', lat: 25.7617, lon: -80.1918, status: 'danger' },
  'TPA': { name: 'Tampa', lat: 27.9506, lon: -82.4572, status: 'warning' },
  'JAX': { name: 'Jacksonville', lat: 30.3322, lon: -81.6557, status: 'active' },
  'MSY': { name: 'New Orleans', lat: 29.9511, lon: -90.0715, status: 'active' },
  'HOU': { name: 'Houston', lat: 29.7604, lon: -95.3698, status: 'active' },
  'CHS': { name: 'Charleston', lat: 32.7765, lon: -79.9311, status: 'active' },
  'ORF': { name: 'Norfolk', lat: 36.8508, lon: -76.2859, status: 'active' },
  'NYC': { name: 'New York', lat: 40.7128, lon: -74.0060, status: 'active' },
  'BOS': { name: 'Boston', lat: 42.3601, lon: -71.0589, status: 'active' },
  'SJU': { name: 'San Juan', lat: 18.4655, lon: -66.1057, status: 'active' }
};

function App() {
  const [storms, setStorms] = useState([]);
  const [stations, setStations] = useState({});
  const [loading, setLoading] = useState(true);
  const [lastUpdate, setLastUpdate] = useState(null);

  useEffect(() => {
    // Fetch data from API service
    const fetchData = async () => {
      setLoading(true);
      try {
        // Use the data service (mock in development, real API in production)
        const [stormsData, stationsData] = await Promise.all([
          dataService.getActiveStorms(),
          dataService.getStations()
        ]);

        setStorms(stormsData);
        setStations(stationsData);
        setLastUpdate(new Date());
      } catch (error) {
        console.error('Error fetching storm data:', error);
        // Fallback to mock data if API fails
        setStorms(mockStormData);
        setStations(mockStations);
        setLastUpdate(new Date());
      } finally {
        setLoading(false);
      }
    };

    fetchData();

    // Set up polling for real-time updates (every 5 minutes)
    const interval = setInterval(fetchData, 5 * 60 * 1000);

    return () => clearInterval(interval);
  }, []);

  const refreshData = async () => {
    setLoading(true);
    try {
      const [stormsData, stationsData] = await Promise.all([
        dataService.getActiveStorms(),
        dataService.getStations()
      ]);

      setStorms(stormsData);
      setStations(stationsData);
      setLastUpdate(new Date());
    } catch (error) {
      console.error('Error refreshing data:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <div className="dashboard">
        <div className="dashboard-header">
          <h1 className="dashboard-title">üå™Ô∏è Cyclone Monitoring Dashboard</h1>
          <p className="dashboard-subtitle">
            Real-time tropical storm tracking and alerts powered by NOAA data
          </p>
          {lastUpdate && (
            <p className="last-update">
              Last updated: {lastUpdate.toLocaleTimeString()}
            </p>
          )}
        </div>

        <div className="dashboard-content">
          {loading ? (
            <div className="loading-container">
              <div className="loading-spinner"></div>
              Fetching latest storm data...
            </div>
          ) : (
            <>
              {/* Active Storm Alerts */}
              <section className="alerts-section">
                <h2 className="section-title">
                  üö® Active Storm Alerts ({storms.length})
                </h2>
                {storms.length > 0 ? (
                  <div className="alerts-grid">
                    {storms.map((storm) => (
                      <AlertCard key={storm.storm_id} storm={storm} />
                    ))}
                  </div>
                ) : (
                  <div className="empty-state">
                    <div className="empty-state-icon">‚úÖ</div>
                    <div className="empty-state-message">No Active Storms</div>
                    <div className="empty-state-submessage">
                      All monitoring areas are currently clear of tropical cyclones
                    </div>
                  </div>
                )}
              </section>

              {/* Interactive Storm Map */}
              <section className="map-section">
                <h2 className="section-title">
                  üó∫Ô∏è Storm Tracking Map
                </h2>
                <div className="map-container">
                  <StormMap storms={storms} stations={stations} />
                </div>
              </section>

              {/* Station Monitoring */}
              <section className="stations-section">
                <h2 className="section-title">
                  üì° Monitoring Stations
                </h2>
                <StationMonitor stations={stations} storms={storms} />
              </section>

              {/* Refresh Button */}
              <div className="refresh-section">
                <button 
                  className="refresh-button"
                  onClick={refreshData}
                  disabled={loading}
                >
                  üîÑ Refresh Data
                </button>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
